mapper.select:
SELECT ID, PW, NAME, EMAIL, PHONE, ADDR, ENABLED, BIRTH, REGDATE, ROLE
FROM MEMBER
WHERE ID = #{id}

SELECT ID, PW, NAME, EMAIL, PHONE, ADDR, ENABLED, BIRTH, REGDATE, ROLE
FROM MEMBER
WHERE EMAIL = #{email}

mapper.update:
UPDATE MEMBER 
SET PW=#{pw}, NAME=#{name}, EMAIL=#{email}, PHONE=#{phone}, ADDR=#{addr}, BIRTH=#{birth} 
WHERE ID=#{id}

mapper.select:
SELECT ID, PW, NAME, EMAIL, PHONE, ADDR, ENABLED, BIRTH, REGDATE, ROLE
FROM MEMBER
WHERE NAME = #{name} AND EMAIL = #{email}

SELECT ID, PW, NAME, EMAIL, PHONE, ADDR, ENABLED, BIRTH, REGDATE, ROLE
FROM MEMBER
WHERE ID = #{id} AND NAME = #{name} AND EMAIL = #{email}

mapper.update:
UPDATE MEMBER
SET PW = #{pw}
WHERE ID = #{id}
